// Función para obtener el contenido de la imagen en base64
function obtenerContenidoBase64($nombre_imagen) {
    global $data;
    if (isset($data[$nombre_imagen]['base64'])) {
        return $data[$nombre_imagen]['base64'];
    }
    return '';
}

