/*--------------------------------------------------------FUNCIONES ADICIONALES------------------------------------------------------------ */
function meses($month){
    if ($month === 'January')  { return 'Enero';     }
    if ($month === 'February') { return 'Febrero';   }
    if ($month === 'March')    { return 'Marzo';     }
    if ($month === 'April')    { return 'Abril';     }
    if ($month === 'May')      { return 'Mayo';      }
    if ($month === 'June')     { return 'Junio';     }
    if ($month === 'July')     { return 'Julio';     }
    if ($month === 'August')   { return 'Agosto';    }
    if ($month === 'September'){ return 'Septiembre';}
    if ($month === 'October')  { return 'Octubre';   }
    if ($month === 'November') { return 'Noviembre'; }
    if ($month === 'December') { return 'Diciembre'; }
    return $month;
}
//crear carpeta conforme el año
function crea_carp_anio($carpetaAnio){
    if (!file_exists($carpetaAnio)){                // si la carpeta no exits en este if se va a crear
        echo '<br>aqui'.$carpetaAnio;
        echo '<br>';
        $carpetaAnio = cambiarBarra($carpetaAnio);
        mkdir($carpetaAnio,0777);                  // Creamos la carpeta si no existe
        return True;
    }else{
        //echo '<br>ya existia la carpeta AÑIO';
        $carpetaAnio = cambiarBarra($carpetaAnio);
        return $carpetaAnio;
    }
}
//crear carpeta conforme el Mes
function crea_carp_mes($carpetaMeses){
    if (!file_exists($carpetaMeses)){               // si la carpeta no exits en este if se va a crear
        $carpetaMeses = cambiarBarra($carpetaMeses);
        mkdir($carpetaMeses,0777);                  // Creamos la carpeta si no existe}
        return $carpetaMeses;
    }else{
        //echo '<br>ya existia la carpeta MES';
        $carpetaMeses = cambiarBarra($carpetaMeses);
        return $carpetaMeses;
    }
}
function cambiarBarra($cadena) {
    // Reemplaza todas las barras diagonales inversas "\" por barras diagonales "/"
    $cadena = str_replace("\\", "/", $cadena);
    return $cadena;
}
